if command -v systemctl >/dev/null 2>&1; then UNIT=''; if systemctl list-unit-files --type=service | grep -q '^prometheus-node-exporter.service'; then UNIT='prometheus-node-exporter.service'; fi; if [ -z "$UNIT" ] && systemctl list-unit-files --type=service | grep -q '^node_exporter.service'; then UNIT='node_exporter.service'; fi; if [ -n "$UNIT" ]; then systemctl is-enabled --quiet "$UNIT" && echo yes || echo no; else echo no; fi; else echo no; fi
